import "./Slide.css";
import React from 'react';
import { useState } from 'react';
import { Card, Image, Typography } from "antd";
import { StarTwoTone } from '@ant-design/icons';
import { Button, Checkbox, Input, Slider } from 'antd';
import Offcanvas from 'react-bootstrap/Offcanvas';
import 'bootstrap/dist/css/bootstrap.min.css';
import { EnvironmentOutlined, SearchOutlined } from '@ant-design/icons';
import MyMap from "../APIs/googlemaps.js";

export function TrailSlide(trail) {
    trail = trail.trail
    let name = trail.name
    let coordinates = {
        latitude: parseFloat(trail.lat),
        longitude: parseFloat(trail.lon)
    }
    // console.log(coordinates)
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <Card className="trailSlide">
            <div className="container">
                <div class="half" > 
                    <h1 className="name"> {trail.name} </h1>
                    <h2 className="name"> {trail.city}, {trail.region} </h2>
                    <h3 className="name"> Difficulty: {trail.difficulty ? trail.difficulty : "N/A"} </h3>
                    <h3 className="name"> Length: {trail.length!="0.0" ? `${trail.length} miles`: "N/A" } </h3>
                    <h3 className="name"> Rating: {trail.rating ? Array.from({ length: trail.rating }).map((_, index) => (
                        <StarTwoTone style={{ marginLeft: '1px' }} /> 
                    )) : "N/A"}  </h3>


                    <div style={{display:"flex"}}>
                        <Button className="map" danger type="primary" icon={<EnvironmentOutlined />} onClick={handleShow}>
                            Map
                        </Button>
                        {trail.url ? 
                            <Button type="primary" onClick={() => window.open(trail.url, '_blank')}>
                                More Info
                            </Button> 
                            : 
                            ""
                        }
                    </div>
                </div>
                <div class="half" > 

                    {trail.thumbnail ? <Image
                        className="thumbnail"
                        src={trail.thumbnail}
                    /> : 
                    <Image 
                        className="thumbnail"
                        src="error"
                        fallback="data:image/png;base64,"
                    />}
                </div>

            </div>
            

            <>
                <Offcanvas style={{width: "430px"}}show={show} onHide={handleClose}>
                    <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Google Map View</Offcanvas.Title>
                    </Offcanvas.Header>
                    <Offcanvas.Body>
                        <MyMap coordinates = {coordinates} name = {name}/>
                    </Offcanvas.Body>
                </Offcanvas>
            </>
        </Card>
    )

    
}

export default TrailSlide;